import {useState, useEffect, Fragment} from "react";
import axios from "axios";
/*
    Map / List  / VO
    {}    []      {}
    {
        list:[],
        curpage:1,
        totalpage:100

    }
 */

function App4() {
    const [music, setMusic] = useState([]);
    const [totalpage, setTotal] = useState(0);
    const [curpage, setCurpage] = useState(1);
    // 변경시마다 HTML에 적용
    // 데이터 읽기 => mounted() => componentDidMount()
    // deps 가 변경이 되는 경우 => setXxx() 함수가 호출되는 경우에 재호출
    useEffect(() => {
        axios.get("http://localhost/music/music_all",{
            params:{
                page:curpage
            }
        }).then(res =>{
            console.log(res.data);
            setMusic(res.data.list);
            setCurpage(res.data.curpage);
            setTotal(res.data.totalpage);
        })
    },[curpage]) // 현재 페이지가 변경되면 다시 수행
    // JavaScript 사용 => for [{},{},{},....]
    /*
        music={
            list:[],
            curpage=1,
            totoalpage:0
        }
     */
    let html=music.map((m,idx)=>
        <div className="col-sm-3"> {/* 주석 */}
            <div className="thumbnail">
                <a href="#">
                    <img src={'http:'+m.poster} style={{"width": "230px","height": "120px"}}/>
                    <div className="caption">
                        <p>{m.title}</p>
                    </div>
                </a>
            </div>
        </div>
    )
    return (
        <Fragment>
            <div className={"row"}>
                {html}
            </div>
            <div style={{"height": "10px"}}></div>
            <div className={"row text-center"}>
                <button className={"btn btn-danger btn-sm"}
                        onClick={()=>setCurpage(curpage>1?curpage-1:curpage)}
                >이전</button>
                {curpage} page / {totalpage} pages
                <button className={"btn btn-danger btn-sm"}
                        onClick={()=>setCurpage(curpage<totalpage?curpage+1:curpage)}
                >다음</button>
            </div>
        </Fragment>
    )
}
export default App4;
